mapper.select:
select messageId
from p_${chatId}
order by messageId desc
LIMIT 1;

select messageId
from g_${chatId}
order by messageId desc
LIMIT 1;

select messageId
from c_${chatId}
order by messageId desc
LIMIT 1;

select messageId, senderId, receiverId, type, text, image, video, audio, file, replyStatus, replyId, createDate, updateDate
from p_${chatId}
where messageId > #{lastMessageId} and status = 0
order by messageId;

select messageId, senderId, receiverId, type, text, image, video, audio, file, replyStatus, replyId, createDate,updateDate
from g_${chatId}
where messageId > #{lastMessageId} and status = 0
order by messageId;

select messageId, senderId, receiverId, type, text, image, video, audio, file, replyStatus, replyId, createDate,updateDate
from c_${chatId}
where messageId > #{lastMessageId} and status = 0
order by messageId;
